		{
			return L"TCPServer";
		}

		String CPackageTCPServer::getDescription()
		{
			return L"TCPServer";
		}

		String CPackageTCPServer::getReferenceUrl()
		{
			return L"";
		}

		const Image *CPackageTCPServer::getIcon()
		{
			return &m_cIcon;
		}

		bool CPackageTCPServer::isVisible()
		{
			return true;
		}

		bool CPackageTCPServer::isInstance(IComponent *component)
		{
			return cast<CTCPServer*>(component) != nullptr;
		}
