		CPackageTimer::CPackageTimer(IPackage *package) : CPackageComponent(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Components/Timer/Timer.h");
		}

		IComponent *CPackageTimer::Create(IPropertyList **properties, IEventList **events)
		{
			IComponent *result{ new CTimer() };
			if (properties)
				*properties = new CTimerPropertyList(getPackage(), result, this);
			if (events)
				*events = new CTimerEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateComponent(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageTimer::CreatePropertyList(IComponent *component)
		{
			if (isInstance(component))
				return new CTimerPropertyList(getPackage(), component, this);
			return nullptr;
		}

		IEventList *CPackageTimer::CreateEventList(IComponent *component)
		{
			if (isInstance(component))
				return new CTimerEventList(getPackage(), component, this);
			return nullptr;
		}

		String CPackageTimer::getCategory()
		{
			return L"Standard";
		}

