		CPackageTrayIcon::CPackageTrayIcon(IPackage *package) : CPackageComponent(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms
			AddPlatform(PlatformVersion{ PlatformType::Windows, L"", L"" });

			// Header files
			AddHeaderFile(L"Standard/Components/TrayIcon/TrayIcon.h");
		}

		IComponent *CPackageTrayIcon::Create(IPropertyList **properties, IEventList **events)
		{
			IComponent *result{ new CTrayIcon() };
			if (properties)
				*properties = new CTrayIconPropertyList(getPackage(), result, this);
			if (events)
				*events = new CTrayIconEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateComponent(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageTrayIcon::CreatePropertyList(IComponent *component)
		{
			if (isInstance(component))
				return new CTrayIconPropertyList(getPackage(), component, this);
			return nullptr;
		}

		IEventList *CPackageTrayIcon::CreateEventList(IComponent *component)
		{
			if (isInstance(component))
				return new CTrayIconEventList(getPackage(), component, this);
			return nullptr;
		}

