		CPackageAlphaBar::CPackageAlphaBar(IPackage *package) : CPackageControl(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Controls/AlphaBar/AlphaBar.h");
		}

		IControl *CPackageAlphaBar::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CAlphaBar() };
			if (properties)
				*properties = new CAlphaBarPropertyList(getPackage(), result, this);
			if (events)
				*events = new CAlphaBarEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageAlphaBar::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CAlphaBarPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageAlphaBar::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CAlphaBarEventList(getPackage(), control, this);
			return nullptr;
		}

