		CPackageCheckBox::CPackageCheckBox(IPackage *package) : CPackageControl(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Controls/CheckBox/CheckBox.h");
		}

		IControl *CPackageCheckBox::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CCheckBox() };
			if (properties)
				*properties = new CCheckBoxPropertyList(getPackage(), result, this);
			if (events)
				*events = new CCheckBoxEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageCheckBox::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CCheckBoxPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageCheckBox::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CCheckBoxEventList(getPackage(), control, this);
			return nullptr;
		}

