		{
			return true;
		}

		bool CPackageCheckBox::isDisplayIcon()
		{
			return false;
		}

		bool CPackageCheckBox::isInstance(IControl *control)
		{
			return cast<CCheckBox*>(control) != nullptr;
		}

		bool CPackageCheckBox::isTransformable()
		{
			return true;
		}
