		CPackageContainer::CPackageContainer(IPackage *package) : CPackageControl(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Controls/Container/Container.h");
		}

		IControl *CPackageContainer::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CContainer() };
			if (properties)
				*properties = new CContainerPropertyList(getPackage(), result, this);
			if (events)
				*events = new CContainerEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageContainer::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CContainerPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageContainer::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CContainerEventList(getPackage(), control, this);
			return nullptr;
		}

		String CPackageContainer::getCategory()
		{
			return L"Standard";
		}

		String CPackageContainer::getClassName()
		{
			return L"Container";
		}

		String CPackageContainer::getTitle()
		{
			return L"Container";
		}

		String CPackageContainer::getDescription()
		{
			return L"Container";
		}

		String CPackageContainer::getReferenceUrl()
		{
			return L"";
		}

		const Image *CPackageContainer::getIcon()
		{
			return &m_cIcon;
		}

		bool CPackageContainer::isVisible()
		{
			return true;
		}

		bool CPackageContainer::isDisplayIcon()
		{
			return false;
		}

		bool CPackageContainer::isInstance(IControl *control)
		{
			return cast<CContainer*>(control) != nullptr;
		}

		bool CPackageContainer::isTransformable()
		{
			return true;
		}
