		CPackageCoolBar::CPackageCoolBar(IPackage *package) : CPackageDialogBox(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Controls/CoolBar/CoolBar.h");

			// DialogBox header files
			AddDialogBoxHeaderFile(L"Standard/Controls/DialogBoxEx/DialogBoxExService.h");
		}

		IControl *CPackageCoolBar::Create(IPropertyList **properties, IEventList **events)
		{
			IDialogBox *result{ new CCoolBar() };
			if (properties)
				*properties = new CCoolBarPropertyList(getPackage(), result, this);
			if (events)
				*events = new CCoolBarEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageCoolBar::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CCoolBarPropertyList(getPackage(), cast<IDialogBox*>(control), this);
			return nullptr;
		}

		IEventList *CPackageCoolBar::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CCoolBarEventList(getPackage(), cast<IDialogBox*>(control), this);
			return nullptr;
		}

