		CPackageDatePicker::CPackageDatePicker(IPackage *package) : CPackageControl(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Controls/DatePicker/DatePicker.h");
		}

		IControl *CPackageDatePicker::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CDatePicker() };
			if (properties)
				*properties = new CDatePickerPropertyList(getPackage(), result, this);
			if (events)
				*events = new CDatePickerEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageDatePicker::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CDatePickerPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageDatePicker::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CDatePickerEventList(getPackage(), control, this);
			return nullptr;
		}

