		CPackageDialogBoxEx::CPackageDialogBoxEx(IPackage *package) : CPackageDialogBox(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Controls/DialogBoxEx/DialogBoxEx.h");

			// DialogBox header files
			AddDialogBoxHeaderFile(L"Standard/Controls/DialogBoxEx/DialogBoxExService.h");
		}

		IControl *CPackageDialogBoxEx::Create(IPropertyList **properties, IEventList **events)
		{
			IDialogBox *result{ new CDialogBoxEx() };
			if (properties)
				*properties = new CDialogBoxExPropertyList(getPackage(), result, this);
			if (events)
				*events = new CDialogBoxExEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageDialogBoxEx::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CDialogBoxExPropertyList(getPackage(), cast<IDialogBox*>(control), this);
			return nullptr;
		}

		IEventList *CPackageDialogBoxEx::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CDialogBoxExEventList(getPackage(), cast<IDialogBox*>(control), this);
			return nullptr;
		}

