		CPackageDirectoryTree::CPackageDirectoryTree(IPackage *package) : CPackageControl(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Controls/DirectoryTree/DirectoryTree.h");
		}

		IControl *CPackageDirectoryTree::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CDirectoryTree() };
			if (properties)
				*properties = new CDirectoryTreePropertyList(getPackage(), result, this);
			if (events)
				*events = new CDirectoryTreeEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageDirectoryTree::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CDirectoryTreePropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageDirectoryTree::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CDirectoryTreeEventList(getPackage(), control, this);
			return nullptr;
		}

