		CPackageDrawGrid::CPackageDrawGrid(IPackage *package) : CPackageCustomDrawGrid(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Controls/DrawGrid/DrawGrid.h");
		}

		IControl *CPackageDrawGrid::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CDrawGrid() };
			if (properties)
				*properties = new CDrawGridPropertyList(getPackage(), result, this);
			if (events)
				*events = new CDrawGridEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageDrawGrid::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CDrawGridPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageDrawGrid::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CDrawGridEventList(getPackage(), control, this);
			return nullptr;
		}

