		IControl *CPackageDriveDropDown::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CDriveDropDown() };
			if (properties)
				*properties = new CDriveDropDownPropertyList(getPackage(), result, this);
			if (events)
				*events = new CDriveDropDownEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageDriveDropDown::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CDriveDropDownPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageDriveDropDown::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CDriveDropDownEventList(getPackage(), control, this);
			return nullptr;
		}

