		CPackageEditCalc::CPackageEditCalc(IPackage *package) : CPackageControl(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Controls/EditCalc/EditCalc.h");
		}

		IControl *CPackageEditCalc::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CEditCalc() };
			if (properties)
				*properties = new CEditCalcPropertyList(getPackage(), result, this);
			if (events)
				*events = new CEditCalcEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageEditCalc::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CEditCalcPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageEditCalc::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CEditCalcEventList(getPackage(), control, this);
			return nullptr;
		}

