		CPackageFormStyleSolid::CPackageFormStyleSolid(IPackage *package) : CPackageControl(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Controls/FormStyleSolid/FormStyleSolid.h");
		}

		IControl *CPackageFormStyleSolid::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CFormStyleSolid() };
			if (properties)
				*properties = new CFormStyleSolidPropertyList(getPackage(), result, this);
			if (events)
				*events = new CFormStyleSolidEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageFormStyleSolid::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CFormStyleSolidPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageFormStyleSolid::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CFormStyleSolidEventList(getPackage(), control, this);
			return nullptr;
		}

