		CPackageFrame::CPackageFrame(IPackage *package) : CPackageControl(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Controls/Frame/Frame.h");
		}

		IControl *CPackageFrame::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CFrame() };
			if (properties)
				*properties = new CFramePropertyList(getPackage(), result, this);
			if (events)
				*events = new CFrameEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageFrame::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CFramePropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageFrame::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CFrameEventList(getPackage(), control, this);
			return nullptr;
		}

