		const Image *CPackageFrame::getIcon()
		{
			return &m_cIcon;
		}

		bool CPackageFrame::isVisible()
		{
			return true;
		}

		bool CPackageFrame::isDisplayIcon()
		{
			return false;
		}

		bool CPackageFrame::isInstance(IControl *control)
		{
			return cast<CFrame*>(control) != nullptr;
		}

		bool CPackageFrame::isTransformable()
		{
			return true;
		}
