		CPackageGradientEditor::CPackageGradientEditor(IPackage *package) : CPackageControl(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Controls/GradientEditor/GradientEditor.h");
		}

		IControl *CPackageGradientEditor::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CGradientEditor() };
			if (properties)
				*properties = new CGradientEditorPropertyList(getPackage(), result, this);
			if (events)
				*events = new CGradientEditorEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageGradientEditor::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CGradientEditorPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageGradientEditor::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CGradientEditorEventList(getPackage(), control, this);
			return nullptr;
		}

