		CPackageGroupBox::CPackageGroupBox(IPackage *package) : CPackageControl(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Controls/GroupBox/GroupBox.h");
		}

		IControl *CPackageGroupBox::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CGroupBox() };
			if (properties)
				*properties = new CGroupBoxPropertyList(getPackage(), result, this);
			if (events)
				*events = new CGroupBoxEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageGroupBox::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CGroupBoxPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageGroupBox::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CGroupBoxEventList(getPackage(), control, this);
			return nullptr;
		}

