		CPackageGroupFold::CPackageGroupFold(IPackage *package) : CPackageControl(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Controls/GroupFold/GroupFold.h");
		}

		IControl *CPackageGroupFold::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CGroupFold() };
			if (properties)
				*properties = new CGroupFoldPropertyList(getPackage(), result, this);
			if (events)
				*events = new CGroupFoldEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageGroupFold::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CGroupFoldPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageGroupFold::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CGroupFoldEventList(getPackage(), control, this);
			return nullptr;
		}

