		CPackageHSVBar::CPackageHSVBar(IPackage *package) : CPackageControl(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Controls/HSVBar/HSVBar.h");
		}

		IControl *CPackageHSVBar::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CHSVBar() };
			if (properties)
				*properties = new CHSVBarPropertyList(getPackage(), result, this);
			if (events)
				*events = new CHSVBarEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageHSVBar::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CHSVBarPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageHSVBar::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CHSVBarEventList(getPackage(), control, this);
			return nullptr;
		}

