		CPackageHSVPlane::CPackageHSVPlane(IPackage *package) : CPackageControl(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Controls/HSVPlane/HSVPlane.h");
		}

		IControl *CPackageHSVPlane::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CHSVPlane() };
			if (properties)
				*properties = new CHSVPlanePropertyList(getPackage(), result, this);
			if (events)
				*events = new CHSVPlaneEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageHSVPlane::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CHSVPlanePropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageHSVPlane::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CHSVPlaneEventList(getPackage(), control, this);
			return nullptr;
		}

