		CPackageHotKey::CPackageHotKey(IPackage *package) : CPackageControl(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Controls/HotKey/HotKey.h");
		}

		IControl *CPackageHotKey::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CHotKey() };
			if (properties)
				*properties = new CHotKeyPropertyList(getPackage(), result, this);
			if (events)
				*events = new CHotKeyEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageHotKey::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CHotKeyPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageHotKey::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CHotKeyEventList(getPackage(), control, this);
			return nullptr;
		}

