		CPackageImage::CPackageImage(IPackage *package) : CPackageControl(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Controls/Image/Image.h");
		}

		IControl *CPackageImage::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CImage() };
			if (properties)
				*properties = new CImagePropertyList(getPackage(), result, this);
			if (events)
				*events = new CImageEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageImage::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CImagePropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageImage::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CImageEventList(getPackage(), control, this);
			return nullptr;
		}

		String CPackageImage::getCategory()
		{
			return L"Standard";
		}

		String CPackageImage::getClassName()
		{
			return L"Image";
		}

