		CPackageImageListEditor::CPackageImageListEditor(IPackage *package) : CPackageControl(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Controls/ImageListEditor/ImageListEditor.h");
		}

		IControl *CPackageImageListEditor::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CImageListEditor() };
			if (properties)
				*properties = new CImageListEditorPropertyList(getPackage(), result, this);
			if (events)
				*events = new CImageListEditorEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageImageListEditor::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CImageListEditorPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageImageListEditor::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CImageListEditorEventList(getPackage(), control, this);
			return nullptr;
		}

