		CPackageLabel::CPackageLabel(IPackage *package) : CPackageControl(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Controls/Label/Label.h");
		}

		IControl *CPackageLabel::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CLabel() };
			if (properties)
				*properties = new CLabelPropertyList(getPackage(), result, this);
			if (events)
				*events = new CLabelEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageLabel::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CLabelPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageLabel::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CLabelEventList(getPackage(), control, this);
			return nullptr;
		}

		String CPackageLabel::getCategory()
		{
			return L"Standard";
		}

