		bool CPackageLabel::isVisible()
		{
			return true;
		}

		bool CPackageLabel::isDisplayIcon()
		{
			return false;
		}

		bool CPackageLabel::isInstance(IControl *control)
		{
			return cast<CLabel*>(control) != nullptr;
		}

		bool CPackageLabel::isTransformable()
		{
			return true;
		}
