		CPackageMaskEdit::CPackageMaskEdit(IPackage *package) : CPackageControl(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Controls/MaskEdit/MaskEdit.h");
		}

		IControl *CPackageMaskEdit::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CMaskEdit() };
			if (properties)
				*properties = new CMaskEditPropertyList(getPackage(), result, this);
			if (events)
				*events = new CMaskEditEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageMaskEdit::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CMaskEditPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageMaskEdit::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CMaskEditEventList(getPackage(), control, this);
			return nullptr;
		}

