		CPackagePageControl::CPackagePageControl(IPackage *package) : CPackageControl(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Controls/PageControl/PageControl.h");
		}

		IControl *CPackagePageControl::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CPageControl() };
			if (properties)
				*properties = new CPageControlPropertyList(getPackage(), result, this);
			if (events)
				*events = new CPageControlEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackagePageControl::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CPageControlPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackagePageControl::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CPageControlEventList(getPackage(), control, this);
			return nullptr;
		}

