		IControl *CPackagePaintBox::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CPaintBox() };
			if (properties)
				*properties = new CPaintBoxPropertyList(getPackage(), result, this);
			if (events)
				*events = new CPaintBoxEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackagePaintBox::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CPaintBoxPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackagePaintBox::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CPaintBoxEventList(getPackage(), control, this);
			return nullptr;
		}

