		CPackagePasswordEdit::CPackagePasswordEdit(IPackage *package) : CPackageControl(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Controls/PasswordEdit/PasswordEdit.h");
		}

		IControl *CPackagePasswordEdit::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CPasswordEdit() };
			if (properties)
				*properties = new CPasswordEditPropertyList(getPackage(), result, this);
			if (events)
				*events = new CPasswordEditEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackagePasswordEdit::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CPasswordEditPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackagePasswordEdit::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CPasswordEditEventList(getPackage(), control, this);
			return nullptr;
		}

