		CPackageProgressBar::CPackageProgressBar(IPackage *package) : CPackageControl(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Controls/ProgressBar/ProgressBar.h");
		}

		IControl *CPackageProgressBar::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CProgressBar() };
			if (properties)
				*properties = new CProgressBarPropertyList(getPackage(), result, this);
			if (events)
				*events = new CProgressBarEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageProgressBar::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CProgressBarPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageProgressBar::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CProgressBarEventList(getPackage(), control, this);
			return nullptr;
		}

		String CPackageProgressBar::getCategory()
		{
			return L"Standard";
		}

