		CPackageRadioButton::CPackageRadioButton(IPackage *package) : CPackageControl(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Controls/RadioButton/RadioButton.h");
		}

		IControl *CPackageRadioButton::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CRadioButton() };
			if (properties)
				*properties = new CRadioButtonPropertyList(getPackage(), result, this);
			if (events)
				*events = new CRadioButtonEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageRadioButton::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CRadioButtonPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageRadioButton::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CRadioButtonEventList(getPackage(), control, this);
			return nullptr;
		}

