		CPackageScrollBar::CPackageScrollBar(IPackage *package) : CPackageControl(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Controls/ScrollBar/ScrollBar.h");
		}

		IControl *CPackageScrollBar::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CScrollBar() };
			if (properties)
				*properties = new CScrollBarPropertyList(getPackage(), result, this);
			if (events)
				*events = new CScrollBarEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageScrollBar::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CScrollBarPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageScrollBar::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CScrollBarEventList(getPackage(), control, this);
			return nullptr;
		}

