		CPackageScrollBarLite::CPackageScrollBarLite(IPackage *package) : CPackageControl(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Controls/ScrollBarLite/ScrollBarLite.h");
		}

		IControl *CPackageScrollBarLite::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CScrollBarLite() };
			if (properties)
				*properties = new CScrollBarLitePropertyList(getPackage(), result, this);
			if (events)
				*events = new CScrollBarLiteEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageScrollBarLite::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CScrollBarLitePropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageScrollBarLite::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CScrollBarLiteEventList(getPackage(), control, this);
			return nullptr;
		}

