		CPackageScrollBox::CPackageScrollBox(IPackage *package) : CPackageControl(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Controls/ScrollBox/ScrollBox.h");
		}

		IControl *CPackageScrollBox::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CScrollBox() };
			if (properties)
				*properties = new CScrollBoxPropertyList(getPackage(), result, this);
			if (events)
				*events = new CScrollBoxEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageScrollBox::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CScrollBoxPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageScrollBox::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CScrollBoxEventList(getPackage(), control, this);
			return nullptr;
		}

