		IControl *CPackageShape::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CShape() };
			if (properties)
				*properties = new CShapePropertyList(getPackage(), result, this);
			if (events)
				*events = new CShapeEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageShape::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CShapePropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageShape::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CShapeEventList(getPackage(), control, this);
			return nullptr;
		}

