		CPackageSpeedButton::CPackageSpeedButton(IPackage *package) : CPackageControl(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Controls/SpeedButton/SpeedButton.h");
		}

		IControl *CPackageSpeedButton::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CSpeedButton() };
			if (properties)
				*properties = new CSpeedButtonPropertyList(getPackage(), result, this);
			if (events)
				*events = new CSpeedButtonEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageSpeedButton::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CSpeedButtonPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageSpeedButton::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CSpeedButtonEventList(getPackage(), control, this);
			return nullptr;
		}

