		CPackageSplitter::CPackageSplitter(IPackage *package) : CPackageControl(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Controls/Splitter/Splitter.h");
		}

		IControl *CPackageSplitter::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CSplitter() };
			if (properties)
				*properties = new CSplitterPropertyList(getPackage(), result, this);
			if (events)
				*events = new CSplitterEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageSplitter::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CSplitterPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageSplitter::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CSplitterEventList(getPackage(), control, this);
			return nullptr;
		}

