		CPackageStringGrid::CPackageStringGrid(IPackage *package) : CPackageCustomStringGrid(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Controls/StringGrid/StringGrid.h");
		}

		IControl *CPackageStringGrid::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CStringGrid() };
			if (properties)
				*properties = new CStringGridPropertyList(getPackage(), result, this);
			if (events)
				*events = new CStringGridEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageStringGrid::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CStringGridPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageStringGrid::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CStringGridEventList(getPackage(), control, this);
			return nullptr;
		}

