		CPackageTimePicker::CPackageTimePicker(IPackage *package) : CPackageControl(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Controls/TimePicker/TimePicker.h");
		}

		IControl *CPackageTimePicker::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CTimePicker() };
			if (properties)
				*properties = new CTimePickerPropertyList(getPackage(), result, this);
			if (events)
				*events = new CTimePickerEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageTimePicker::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CTimePickerPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageTimePicker::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CTimePickerEventList(getPackage(), control, this);
			return nullptr;
		}

		String CPackageTimePicker::getCategory()
		{
			return L"Additional";
		}

