		CPackageToggle::CPackageToggle(IPackage *package) : CPackageControl(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Controls/Toggle/Toggle.h");
		}

		IControl *CPackageToggle::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CToggle() };
			if (properties)
				*properties = new CTogglePropertyList(getPackage(), result, this);
			if (events)
				*events = new CToggleEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageToggle::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CTogglePropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageToggle::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CToggleEventList(getPackage(), control, this);
			return nullptr;
		}

		String CPackageToggle::getCategory()
		{
			return L"Additional";
		}

		String CPackageToggle::getClassName()
		{
			return L"Toggle";
		}

		String CPackageToggle::getTitle()
		{
			return L"Toggle";
		}

		String CPackageToggle::getDescription()
		{
			return L"Toggle";
		}

		String CPackageToggle::getReferenceUrl()
		{
			return L"";
		}

		const Image *CPackageToggle::getIcon()
		{
			return &m_cIcon;
		}

		bool CPackageToggle::isVisible()
		{
			return true;
		}

		bool CPackageToggle::isDisplayIcon()
		{
			return false;
		}

		bool CPackageToggle::isInstance(IControl *control)
		{
			return cast<CToggle*>(control) != nullptr;
		}

		bool CPackageToggle::isTransformable()
		{
			return true;
		}
