		CPackageToolBar::CPackageToolBar(IPackage *package) : CPackageControl(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Controls/ToolBar/ToolBar.h");
		}

		IControl *CPackageToolBar::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CToolBar() };
			if (properties)
				*properties = new CToolBarPropertyList(getPackage(), result, this);
			if (events)
				*events = new CToolBarEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageToolBar::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CToolBarPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageToolBar::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CToolBarEventList(getPackage(), control, this);
			return nullptr;
		}

