		bool CPackageToolBar::isVisible()
		{
			return true;
		}

		bool CPackageToolBar::isDisplayIcon()
		{
			return false;
		}

		bool CPackageToolBar::isInstance(IControl *control)
		{
			return cast<CToolBar*>(control) != nullptr;
		}

		bool CPackageToolBar::isTransformable()
		{
			return true;
		}
