		CPackageTrackBar::CPackageTrackBar(IPackage *package) : CPackageControl(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Controls/TrackBar/TrackBar.h");
		}

		IControl *CPackageTrackBar::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CTrackBar() };
			if (properties)
				*properties = new CTrackBarPropertyList(getPackage(), result, this);
			if (events)
				*events = new CTrackBarEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageTrackBar::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CTrackBarPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageTrackBar::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CTrackBarEventList(getPackage(), control, this);
			return nullptr;
		}

