		CPackageTrackRange::CPackageTrackRange(IPackage *package) : CPackageControl(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Controls/TrackRange/TrackRange.h");
		}

		IControl *CPackageTrackRange::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CTrackRange() };
			if (properties)
				*properties = new CTrackRangePropertyList(getPackage(), result, this);
			if (events)
				*events = new CTrackRangeEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageTrackRange::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CTrackRangePropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageTrackRange::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CTrackRangeEventList(getPackage(), control, this);
			return nullptr;
		}

