		CPackageTreeView::CPackageTreeView(IPackage *package) : CPackageCustomTreeView(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Controls/TreeView/TreeView.h");
			AddHeaderFile(L"Standard/ListItems/TreeViewLabel/ListItemTreeViewLabel.h");
		}

		IControl *CPackageTreeView::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CTreeView() };
			if (properties)
				*properties = new CTreeViewPropertyList(getPackage(), result, this);
			if (events)
				*events = new CTreeViewEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageTreeView::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CTreeViewPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageTreeView::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CTreeViewEventList(getPackage(), control, this);
			return nullptr;
		}

