		bool CPackageTreeView::isVisible()
		{
			return true;
		}

		bool CPackageTreeView::isDisplayIcon()
		{
			return false;
		}

		bool CPackageTreeView::isInstance(IControl *control)
		{
			return cast<CTreeView*>(control) != nullptr;
		}

		bool CPackageTreeView::CreateListItem(IComponent *component, IListItem *parent, IListItem **item, IPackageControlListener *listener)
		{
			if (component && (item || listener))
			{
				ITreeView *control{ cast<ITreeView*>(component) };
				if (control)
				{
					if (!parent)
						parent = control->getRootItem();
					IListItem *i{ new CListItemTreeViewLabel(L"Item") };
					if (item)
						*item = i;
					parent->AddItem(i);
					if(listener)
						listener->OnCreateListItem(this, parent, i);
					return true;
				}
			}
			return false;
		}

		bool CPackageTreeView::isTransformable()
		{
			return true;
		}
