		CPackageUpDown::CPackageUpDown(IPackage *package) : CPackageControl(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Controls/UpDown/UpDown.h");
		}

		IControl *CPackageUpDown::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CUpDown() };
			if (properties)
				*properties = new CUpDownPropertyList(getPackage(), result, this);
			if (events)
				*events = new CUpDownEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageUpDown::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CUpDownPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageUpDown::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CUpDownEventList(getPackage(), control, this);
			return nullptr;
		}

