		CPackageUpDownEx::CPackageUpDownEx(IPackage *package) : CPackageControl(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Controls/UpDownEx/UpDownEx.h");
		}

		IControl *CPackageUpDownEx::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CUpDownEx() };
			if (properties)
				*properties = new CUpDownExPropertyList(getPackage(), result, this);
			if (events)
				*events = new CUpDownExEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageUpDownEx::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CUpDownExPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageUpDownEx::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CUpDownExEventList(getPackage(), control, this);
			return nullptr;
		}

